// Calculate today and yesterday dates
const today = new Date();
const yesterday = new Date(today);
yesterday.setDate(yesterday.getDate() - 1);

const todayStr = today.toISOString().split('T')[0];
const yesterdayStr = yesterday.toISOString().split('T')[0];

// Filter leads
const newLeads = items.filter(item => {
  const dateFound = item.json['Date Found'];
  return dateFound && dateFound.includes(yesterdayStr);
});

const followupsToday = items.filter(item => {
  const followupDate = item.json['Next Follow-up Date'];
  return followupDate && followupDate.includes(todayStr);
});

const appointmentsToday = items.filter(item => {
  const status = item.json['Status'] || '';
  const followupDate = item.json['Next Follow-up Date'];
  return status.toLowerCase().includes('appointment') && 
         followupDate && followupDate.includes(todayStr);
});

const totalLeads = items.length;
const contacted = items.filter(item => {
  const status = item.json['Status'] || '';
  return status.toLowerCase().includes('contacted') || 
         status.toLowerCase().includes('appointment');
}).length;

const newLeadsList = newLeads.map(item => 
  `â€¢ ${item.json['Owner Name']} - ${item.json['Property Address']} (${item.json['Phone Number'] || 'No phone'})`
).join('\n');

const followupsList = followupsToday.map(item =>
  `â€¢ ${item.json['Owner Name']} - ${item.json['Phone Number']} - ${item.json['Status']}`
).join('\n');

const appointmentsList = appointmentsToday.map(item =>
  `â€¢ ${item.json['Owner Name']} - ${item.json['Phone Number']} at ${item.json['Next Follow-up Date']}`
).join('\n');

return [
  {
    json: {
      date: todayStr,
      stats: {
        newLeadsCount: newLeads.length,
        followupsCount: followupsToday.length,
        appointmentsCount: appointmentsToday.length,
        totalLeads: totalLeads,
        contactedCount: contacted,
        contactRate: totalLeads > 0 ? Math.round((contacted / totalLeads) * 100) : 0
      },
      lists: {
        newLeads: newLeadsList || 'None',
        followups: followupsList || 'None',
        appointments: appointmentsList || 'None'
      }
    }
  }
];
